// Load company map from environment variable (JSON string)
const companyMap = JSON.parse($env["COMPANY_MAP"]);

// Convert to arrays of valid names
const validCompanies = Object.values(companyMap).map(v => v.toLowerCase());
const validKeys = Object.keys(companyMap).map(k => k.toLowerCase());

// Keep only items whose company matches either a key or value in COMPANY_MAP
const filtered = items.filter(item => {
